import { NextRequest, NextResponse } from 'next/server';
import { createClient } from '@supabase/supabase-js';
import { createSupabaseServerClient } from '@/lib/supabaseServer';

export async function GET(request: NextRequest) {
  try {
    const supabase = await createSupabaseServerClient();
    const {
      data: { user },
    } = await supabase.auth.getUser();

    if (!user) {
      return NextResponse.json(
        { error: 'Unauthorized: Please log in first' },
        { status: 401 }
      );
    }

    // Get all modules and lessons
    const { data: modules } = await supabase
      .from('modules')
      .select('id, number, title')
      .order('number', { ascending: true });

    if (!modules) {
      return NextResponse.json(
        { error: 'Failed to fetch modules' },
        { status: 500 }
      );
    }

    // Get all lessons with their module info
    const { data: lessons } = await supabase
      .from('lessons')
      .select('id, module_id, order, title')
      .order('module_id, order', { ascending: true });

    if (!lessons) {
      return NextResponse.json(
        { error: 'Failed to fetch lessons' },
        { status: 500 }
      );
    }

    // Get user's lesson progress
    const { data: progress } = await supabase
      .from('lesson_progress')
      .select('lesson_id, completed, completed_at')
      .eq('user_id', user.id);

    if (!progress) {
      return NextResponse.json(
        { error: 'Failed to fetch progress' },
        { status: 500 }
      );
    }

    // Get homework submissions for all modules
    const { data: homework } = await supabase
      .from('homework_submissions')
      .select('lesson_id, submitted_at, status')
      .eq('user_id', user.id);

    // Build detailed report
    const report = (modules as any[]).map((module) => {
      const moduleLessons = (lessons as any[]).filter(
        (l) => l.module_id === module.id
      );

      const lessonsData = moduleLessons.map((lesson) => {
        const progressRecord = progress.find(
          (p) => p.lesson_id === lesson.id
        );
        const homeworkRecord = homework?.find(
          (h) => h.lesson_id === lesson.id
        );

        return {
          id: lesson.id,
          order: lesson.order,
          title: lesson.title,
          completed: progressRecord?.completed ?? false,
          completed_at: progressRecord?.completed_at ?? null,
          homework_submitted: !!homeworkRecord,
          homework_status: homeworkRecord?.status ?? null,
        };
      });

      const allCompleted = lessonsData.every((l) => l.completed);
      const moduleHomework = homework?.find((h) =>
        moduleLessons.some((l) => l.id === h.lesson_id)
      );

      return {
        module_id: module.id,
        module_number: module.number,
        module_title: module.title,
        total_lessons: moduleLessons.length,
        completed_lessons: lessonsData.filter((l) => l.completed).length,
        all_lessons_completed: allCompleted,
        lessons: lessonsData,
        module_homework_submitted: !!moduleHomework,
      };
    });

    return NextResponse.json(
      {
        user_id: user.id,
        user_email: user.email,
        modules_report: report,
      },
      { status: 200 }
    );
  } catch (error) {
    console.error('Debug endpoint error:', error);
    return NextResponse.json(
      { error: 'Internal server error' },
      { status: 500 }
    );
  }
}

