import { requireAdmin } from '@/lib/auth';
import { createSupabaseServerClient } from '@/lib/supabaseServer';
import HomeworkList from './HomeworkList';
import FilterSection from './FilterSection';

export type Submission = {
  id: string;
  lesson_id: string;
  user_id: string;
  submission_text: string | null;
  submitted_at: string;
  feedback: string | null;
  status: 'SUBMITTED' | 'REVIEWED' | 'APPROVED';
  full_name: string | null;
  email: string | null;
  lesson_title: string;
};

async function fetchSubmissions(lessonFilter?: string, statusFilter?: string): Promise<Submission[]> {
  const supabase = await createSupabaseServerClient();

  // Build the query with joins
  let query = supabase
    .from('homework_submissions')
    .select(
      `id,
       lesson_id,
       user_id,
       submission_text,
       submitted_at,
       feedback,
       status,
       users_profile!homework_submissions_user_id_fkey(full_name, email),
       lessons!homework_submissions_lesson_id_fkey(id, title)`
    )
    .order('submitted_at', { ascending: false });

  // Apply filters if provided
  if (lessonFilter && lessonFilter !== 'all') {
    query = query.eq('lesson_id', lessonFilter);
  }

  if (statusFilter && statusFilter !== 'all') {
    query = query.eq('status', statusFilter);
  }

  const { data, error } = await query;

  if (error) {
    throw new Error(`Failed to fetch submissions: ${error.message}`);
  }

  if (!data || data.length === 0) {
    return [];
  }

  // Transform the data to flat structure
  type SubmissionData = {
    id: string;
    lesson_id: string;
    user_id: string;
    submission_text: string | null;
    submitted_at: string;
    feedback: string | null;
    status: string;
    users_profile?: { full_name: string | null; email: string | null }[] | null;
    lessons?: { id: string; title: string }[] | null;
  };
  const submissions: Submission[] = (data as SubmissionData[]).map((item) => {
    const userProfile = item.users_profile?.[0];
    const lesson = item.lessons?.[0];

    return {
      id: item.id,
      lesson_id: item.lesson_id,
      user_id: item.user_id,
      submission_text: item.submission_text,
      submitted_at: item.submitted_at,
      feedback: item.feedback,
      status: item.status as 'SUBMITTED' | 'REVIEWED' | 'APPROVED',
      full_name: userProfile?.full_name ?? null,
      email: userProfile?.email ?? null,
      lesson_title: lesson?.title || 'Unknown Lesson',
    };
  });

  return submissions;
}

async function fetchAllLessons() {
  const supabase = await createSupabaseServerClient();

  const { data, error } = await supabase
    .from('lessons')
    .select('id, title')
    .order('created_at', { ascending: true });

  if (error) {
    throw new Error(`Failed to fetch lessons: ${error.message}`);
  }

