/* ==================== RESPONSIVE ==================== */
@media (max-width: 768px) {
    h1 {
        font-size: 2.5rem;
    }

    h2 {
        font-size: 1.8rem;
    }

    h3 {
        font-size: 1.4rem;
    }

    p {
        font-size: 1rem;
    }

    section {
        padding: 3rem 0;
    }

    .container {
        padding: 0 1rem;
    }

    .price-amount {
        font-size: 2.5rem;
    }

    .final-cta p {
        font-size: 1.1rem;
    }

    /* Mobile-specific optimizations */
    .btn {
        padding: 0.9rem 1.2rem;
        font-size: 0.95rem;
    }

    .pricing-card {
        margin: 1.5rem auto;
        padding: 1.25rem;
    }

    .includes-list {
        gap: 0.5rem;
        margin: 1.25rem 0;
    }

    .proof-avatar {
        width: 45px;
        height: 45px;
        font-size: 1rem;
    }

    .proof-info h4 {
        font-size: 0.9rem;
    }

    .proof-info p {
        font-size: 0.75rem;
    }

    .proof-text {
        font-size: 0.8rem;
    }

    .stars {
        font-size: 0.9rem;
        margin-bottom: 0.5rem;
    }

    .benefit-item {
        padding: 0.75rem;
    }

    .benefit-icon {
        font-size: 1.25rem;
    }

    .benefit-text h4 {
        font-size: 0.9rem;
    }

    .benefit-text p {
        font-size: 0.8rem;
    }

    .feature-icon {
        font-size: 1.5rem;
        margin-bottom: 0.5rem;
    }

    .feature h4 {
        font-size: 0.95rem;
    }

    .feature p {
        font-size: 0.75rem;
    }

    .step {
        padding: 1rem 1rem 1rem 2.5rem;
    }

    .step::before {
        width: 35px;
        height: 35px;
        font-size: 1.1rem;
    }

    .step h4 {
        font-size: 0.95rem;
    }

    .step p {
        font-size: 0.8rem;
    }

    .problem-item h4 {
        font-size: 0.95rem;
    }

    .problem-item p {
        font-size: 0.8rem;
    }

    .use-case h4 {
        font-size: 0.95rem;
    }

    .use-case p {
        font-size: 0.8rem;
    }

    .offer-item h4 {
        font-size: 0.95rem;
    }

    .offer-item p {
        font-size: 0.8rem;
    }

    .reason h4 {
        font-size: 1rem;
    }

    .reason p {
        font-size: 0.85rem;
        line-height: 1.6;
    }

    .faq-question {
        padding: 0.9rem;
        font-size: 0.9rem;
    }

    .faq-answer {
        padding: 0.9rem;
        font-size: 0.8rem;
        line-height: 1.6;
    }

    .logo-img {
        height: 48px;
    }

    .login-btn {
        padding: 0.5rem 0.95rem;
        font-size: 0.9rem;
    }

    @media (max-width: 480px) {
        h1 {
            font-size: 1.75rem;
        }

        h2 {
            font-size: 1.4rem;
        }

        h3 {
            font-size: 1.2rem;
        }

        p {
            font-size: 0.9rem;
        }

        .btn {
            padding: 0.8rem 1rem;
            font-size: 0.9rem;
        }

        section {
            padding: 2rem 0;
        }

        .hero {
            padding: 1.5rem 0;
        }

        .hero-title {
            margin-top: 2rem;
            margin-bottom: 2rem;
        }

        .hero-subtitle {
            font-size: 0.9rem;
            margin-bottom: 1rem;
        }

        .hero-cta {
            margin-top: 1rem;
        }

        .price-amount {
            font-size: 2rem;
        }

        .price-subtitle {
            font-size: 0.9rem;
        }

        .benefit-item {
            padding: 0.65rem;
            gap: 0.5rem;
        }

        .benefit-icon {
            font-size: 1.1rem;
        }

        .benefit-text h4 {
            font-size: 0.85rem;
        }

        .benefit-text p {
            font-size: 0.75rem;
        }

        .static-features {
            grid-template-columns: 1fr;
            gap: 1rem;
        }

        .feature {
            padding: 1rem;
        }

        .feature-icon {
            font-size: 1.25rem;
            margin-bottom: 0.5rem;
        }

        .feature h4 {
            font-size: 0.9rem;
        }

        .feature p {
            font-size: 0.7rem;
        }

        .problem-grid {
            gap: 0.75rem;
            margin-top: 1rem;
        }

        .problem-item {
            padding: 1rem;
            border-radius: 0.5rem;
        }

        .problem-item h4 {
            font-size: 0.9rem;
            margin-bottom: 0.35rem;
        }

        .problem-item p {
            font-size: 0.75rem;
        }

        .use-cases {
            gap: 0.75rem;
            margin-top: 1rem;
        }

        .use-case {
            padding: 1rem;
            border-radius: 0.5rem;
        }

        .use-case h4 {
            font-size: 0.9rem;
            margin-bottom: 0.5rem;
        }

        .use-case p {
            font-size: 0.75rem;
        }

        .offer-grid {
            gap: 0.75rem;
            margin-top: 1rem;
        }

        .offer-item {
            padding: 1rem;
            border-radius: 0.5rem;
            text-align: center;
        }

        .offer-item h4 {
            font-size: 0.9rem;
            margin-bottom: 0.4rem;
        }

        .offer-item p {
            font-size: 0.75rem;
        }

        .step {
            padding: 0.9rem 0.9rem 0.9rem 2.2rem;
        }

        .step::before {
            width: 32px;
            height: 32px;
            font-size: 1rem;
            left: 0.5rem;
        }

        .step h4 {
            margin-top: 0;
            font-size: 0.9rem;
        }

        .step p {
            font-size: 0.75rem;
        }

        .proof-card {
            padding: 1rem;
            border-radius: 0.5rem;
        }

        .proof-header {
            gap: 0.6rem;
            margin-bottom: 0.6rem;
        }

        .proof-avatar {
            width: 40px;
            height: 40px;
            font-size: 0.9rem;
        }

        .proof-info h4 {
            font-size: 0.85rem;
            margin: 0 0 0.15rem 0;
        }

        .proof-info p {
            font-size: 0.7rem;
        }

        .stars {
            font-size: 0.85rem;
            margin-bottom: 0.4rem;
        }

        .proof-text {
            font-size: 0.75rem;
            line-height: 1.5;
        }

        .pricing-card {
            margin: 1rem auto;
            padding: 1rem;
            border-radius: 0.75rem;
            border: 2px solid var(--golden);
        }

        .price-amount {
            font-size: 1.8rem;
            margin-bottom: 0.4rem;
        }

        .includes-item {
            gap: 0.6rem;
            padding: 0.65rem;
            border-radius: 0.4rem;
        }

        .includes-icon {
            font-size: 1.1rem;
            min-width: 22px;
        }

        .includes-item strong {
            font-size: 0.85rem;
        }

        .includes-item p {
            font-size: 0.7rem;
        }

        .faq-item {
            border-radius: 0.5rem;
        }

        .faq-question {
            padding: 0.85rem;
            font-size: 0.85rem;
        }

        .faq-toggle {
            font-size: 1.1rem;
            margin-left: 0.75rem;
        }

        .faq-answer {
            padding: 0.85rem;
            font-size: 0.75rem;
            line-height: 1.5;
        }

        .final-cta {
            padding: 1.5rem 0;
        }

        .final-cta h2 {
            margin-bottom: 0.75rem;
        }

        .final-cta p {
            font-size: 0.95rem;
            margin-bottom: 1rem;
        }

        .reason {
            padding: 0.75rem;
            border-radius: 0.5rem;
            text-align: center;
        }

        .reason h4 {
            font-size: 0.95rem;
            margin-bottom: 0.4rem;
        }

        .reason p {
            font-size: 0.8rem;
            line-height: 1.5;
        }

        .mini-separator {
            width: 70px;
            height: 3px;
            margin: 1rem auto;
        }

        footer {
            padding: 1.5rem 0;
        }

        .footer-text {
            font-size: 0.75rem;
        }
    }
}

