/* ==================== SOCIAL PROOF ==================== */
.social-proof {
    background: linear-gradient(135deg, var(--bg-secondary) 0%, var(--bg-primary) 100%);
}

.proof-grid {
    display: grid;
    grid-template-columns: 1fr;
    gap: 1rem;
    margin-top: 1.5rem;
    max-width: 1000px;
    margin-left: auto;
    margin-right: auto;
}

.proof-card {
    padding: 1.25rem;
    background: white;
    border-radius: 0.75rem;
    border: 1px solid var(--border-light);
    box-shadow: 0 4px 12px rgba(0, 0, 1, 0.05);
    transition: all 0.3s ease;
}

.proof-card:hover {
    box-shadow: 0 12px 32px rgba(0, 0, 1, 0.1);
    transform: translateY(-4px);
}

@media (min-width: 768px) {
    .proof-grid {
        grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
        gap: 2rem;
        margin-top: 3rem;
    }

    .proof-card {
        padding: 2rem;
        border-radius: 1rem;
    }
}

.proof-header {
    display: flex;
    gap: 0.75rem;
    margin-bottom: 0.75rem;
}

.proof-avatar {
    width: 50px;

