.proof-grid {
    display: flex;
    gap: 1rem;
    margin-top: 1.5rem;
    max-width: 1000px;
    margin-left: auto;
    margin-right: auto;
    overflow-x: auto;
    scroll-behavior: smooth;
    scroll-snap-type: x mandatory;
    padding-bottom: 1rem;
    position: relative;
}

.proof-grid::-webkit-scrollbar {
    height: 6px;
}

.proof-grid::-webkit-scrollbar-track {
    background: var(--bg-secondary);
    border-radius: 10px;
}

.proof-grid::-webkit-scrollbar-thumb {
    background: var(--golden);
    border-radius: 10px;
}

.proof-grid::-webkit-scrollbar-thumb:hover {
    background: #fcc200;
}

.proof-card {
    flex: 0 0 calc(100% - 1rem);
    min-width: 280px;
    padding: 1.25rem;
    background: white;
    border-radius: 0.75rem;
    border: 1px solid var(--border-light);
    box-shadow: 0 4px 12px rgba(0, 0, 1, 0.05);
    transition: all 0.3s ease;
    scroll-snap-align: start;
}

.proof-card:hover {
    box-shadow: 0 12px 32px rgba(0, 0, 1, 0.1);
    transform: translateY(-4px);
}

.carousel-controls {
    display: flex;
    gap: 1rem;
    justify-content: center;
    margin-top: 1.5rem;
}

.carousel-btn {
    width: 40px;
    height: 40px;
    border-radius: 50%;
    background: var(--golden);
    color: var(--dark);
    border: none;
    cursor: pointer;
    font-size: 1.2rem;
    display: flex;
    align-items: center;
    justify-content: center;
    transition: all 0.3s ease;
    box-shadow: 0 4px 12px rgba(254, 191, 0, 0.25);
}

.carousel-btn:hover {
    background: #fcc200;
    transform: scale(1.1);
    box-shadow: 0 6px 16px rgba(254, 191, 0, 0.35);
}

.carousel-btn:disabled {
    opacity: 0.5;
    cursor: not-allowed;
    transform: scale(1);
}

@media (min-width: 768px) {
    .proof-grid {
        gap: 2rem;
        margin-top: 3rem;
        padding-bottom: 1.5rem;
    }

    .proof-card {
        flex: 0 0 calc(33.333% - 1.35rem);
        min-width: 300px;
        padding: 2rem;
        border-radius: 1rem;
    }

    .carousel-controls {
        margin-top: 2rem;
    }

    .carousel-btn {
        width: 45px;
        height: 45px;
        font-size: 1.3rem;
    }
}

@media (max-width: 480px) {
    .proof-card {
        flex: 0 0 calc(100% - 1rem);
        min-width: 280px;
    }
}

