     1→import { NextRequest, NextResponse } from 'next/server';
     2→import { createClient } from '@supabase/supabase-js';
     3→
     4→// Type definitions
     5→interface CreateUserRequest {
     6→  email: string;
     7→  password: string;
     8→  full_name: string;
     9→  role: string;
    10→}
    11→
    12→interface ValidationError {
    13→  [key: string]: string;
    14→}
    15→
    16→interface SuccessResponse {
    17→  id: string;
    18→  email: string;
    19→  full_name: string;
    20→  role: string;
    21→}
    22→
    23→interface ErrorResponse {
    24→  error: string;
    25→  details?: ValidationError;
    26→}
    27→
    28→// Helper: Validate email format
    29→function isValidEmail(email: string): boolean {
    30→  const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    31→  return emailRegex.test(email);
    32→}
    33→
    34→// Helper: Validate request body
    35→function validateInput(body: unknown): { valid: boolean; errors?: ValidationError; data?: CreateUserRequest } {
    36→  if (!body || typeof body !== 'object') {
    37→    return { valid: false, errors: { body: 'Request body must be valid JSON' } };
    38→  }
    39→
    40→  const input = body as Record<string, unknown>;
    41→  const errors: ValidationError = {};
    42→
    43→  // Email validation
    44→  if (!input.email || typeof input.email !== 'string' || !isValidEmail(input.email)) {
    45→    errors.email = 'Email is required and must be a valid email address';
    46→  }
    47→
    48→  // Password validation (at least 8 characters)
    49→  if (!input.password || typeof input.password !== 'string' || input.password.length < 8) {
    50→    errors.password = 'Password is required and must be at least 8 characters';
    51→  }
    52→
    53→  // Full name validation
    54→  if (!input.full_name || typeof input.full_name !== 'string' || input.full_name.trim().length === 0) {
    55→    errors.full_name = 'Full name is required and cannot be empty';
    56→  }
    57→
    58→  // Role validation
    59→  if (!input.role || typeof input.role !== 'string' || input.role.trim().length === 0) {
    60→    errors.role = 'Role is required and cannot be empty';
    61→  }
    62→
    63→  if (Object.keys(errors).length > 0) {
    64→    return { valid: false, errors };
    65→  }
    66→
    67→  return {
    68→    valid: true,
    69→    data: {
    70→      email: (input.email as string).toLowerCase(),
    71→      password: input.password as string,
    72→      full_name: (input.full_name as string).trim(),
    73→      role: (input.role as string).toLowerCase().trim(),
    74→    },
    75→  };
    76→}
    77→
    78→// Helper: Verify bearer token
    79→function verifyBearerToken(authHeader: string | null): boolean {
    80→  if (!authHeader) return false;
    81→
    82→  const expectedToken = process.env.ADMIN_BEARER_TOKEN;
    83→  if (!expectedToken) {
    84→    console.warn('⚠️ ADMIN_BEARER_TOKEN not configured');
    85→    return false;
    86→  }
    87→
    88→  const parts = authHeader.split(' ');
    89→  if (parts.length !== 2 || parts[0] !== 'Bearer') {
    90→    return false;
    91→  }
    92→
    93→  const token = parts[1];
    94→  return token === expectedToken;
    95→}
    96→
    97→// Type definitions for GET response
    98→interface ListUserResponse {
    99→  id: string;
   100→  email: string;
   101→  full_name: string;
   102→  role: string;
   103→  is_active: boolean;
   104→  created_at: string;
   105→}
   106→
   107→interface ListUsersResponse {
   108→  data: ListUserResponse[];
   109→  pagination: {
   110→    total: number;
   111→    limit: number;
   112→    offset: number;
   113→    has_more: boolean;
   114→  };
   115→}
   116→
   117→// Helper: Validate query parameters for GET
   118→function validateListQuery(searchParams: URLSearchParams): { valid: boolean; errors?: string; limit?: number; offset?: number; role?: string; is_active?: boolean; search?: string; sort_by?: string; sort_order?: string } {
   119→  const limit = Math.min(parseInt(searchParams.get('limit') || '10'), 100);
   120→  const offset = Math.max(parseInt(searchParams.get('offset') || '0'), 0);
   121→  const role = searchParams.get('role');
   122→  const is_active = searchParams.get('is_active');
   123→  const search = searchParams.get('search');
   124→  const sort_by = searchParams.get('sort_by') || 'created_at';
   125→  const sort_order = searchParams.get('sort_order') || 'desc';
   126→
   127→  // Validate limit
   128→  if (isNaN(limit) || limit < 1) {
   129→    return { valid: false, errors: 'limit must be a positive number (max 100)' };
   130→  }
   131→
   132→  // Validate offset
   133→  if (isNaN(offset) || offset < 0) {
   134→    return { valid: false, errors: 'offset must be a non-negative number' };
   135→  }
   136→
   137→  // Validate role
   138→  if (role && !['admin', 'student', 'instructor'].includes(role.toLowerCase())) {
   139→    return { valid: false, errors: 'role must be admin, student, or instructor' };
   140→  }
   141→
   142→  // Validate is_active
   143→  let is_active_parsed: boolean | undefined;
   144→  if (is_active !== null) {
   145→    if (!['true', 'false'].includes(is_active.toLowerCase())) {
   146→      return { valid: false, errors: 'is_active must be true or false' };
   147→    }
   148→    is_active_parsed = is_active.toLowerCase() === 'true';
   149→  }
   150→
   151→  // Validate sort_by
   152→  if (!['created_at', 'email', 'full_name'].includes(sort_by)) {
   153→    return { valid: false, errors: 'sort_by must be created_at, email, or full_name' };
   154→  }
   155→
   156→  // Validate sort_order
   157→  if (!['asc', 'desc'].includes(sort_order.toLowerCase())) {
   158→    return { valid: false, errors: 'sort_order must be asc or desc' };
   159→  }
   160→
   161→  return {
   162→    valid: true,
   163→    limit,
   164→    offset,
   165→    role: role?.toLowerCase() || undefined,
   166→    is_active: is_active_parsed,
   167→    search: search || undefined,
   168→    sort_by,
   169→    sort_order: sort_order.toLowerCase(),
   170→  };
   171→}
   172→
   173→// Main GET handler - List users
   174→export async function GET(request: NextRequest): Promise<NextResponse<ListUsersResponse | ErrorResponse>> {
   175→  try {
   176→    // 1. Check authorization
   177→    const authHeader = request.headers.get('Authorization');
   178→    if (!verifyBearerToken(authHeader)) {
   179→      return NextResponse.json(
   180→        { error: 'Unauthorized' },
   181→        { status: 401 }
   182→      );
   183→    }
   184→
   185→    // 2. Validate query parameters
   186→    const { searchParams } = new URL(request.url);
   187→    const validation = validateListQuery(searchParams);
   188→    if (!validation.valid) {
   189→      return NextResponse.json(
   190→        { error: 'Invalid query parameters', details: { params: validation.errors || 'Invalid parameters' } },
   191→        { status: 400 }
   192→      ) as NextResponse<ListUsersResponse | ErrorResponse>;
   193→    }
   194→
   195→    const { limit: pageLimit, offset: pageOffset, role: filterRole, is_active: filterActive, search: searchTerm, sort_by: sortBy, sort_order: sortOrder } = validation;
   196→
   197→    // Ensure values are not undefined
   198→    const limit = pageLimit || 10;
   199→    const offset = pageOffset || 0;
   200→    const role = filterRole;
   201→    const is_active = filterActive;
   202→    const search = searchTerm;
   203→    const sort_by = sortBy || 'created_at';
   204→    const sort_order = sortOrder || 'desc';
   205→
   206→    // 3. Create Supabase client
   207→    const supabaseUrl = process.env.NEXT_PUBLIC_SUPABASE_URL;
   208→    const serviceRoleKey = process.env.SUPABASE_SERVICE_ROLE_KEY;
   209→
   210→    if (!supabaseUrl || !serviceRoleKey) {
   211→      console.error('❌ Missing Supabase environment variables');
   212→      return NextResponse.json(
   213→        { error: 'Internal server error' },
   214→        { status: 500 }
   215→      );
   216→    }
   217→
   218→    const supabase = createClient(supabaseUrl, serviceRoleKey, {
   219→      auth: {
   220→        autoRefreshToken: false,
   221→        persistSession: false,
   222→      },
   223→    });
   224→
   225→    // 4. Build query
