   241→      query = query.or(`email.ilike.%${searchLower}%,full_name.ilike.%${searchLower}%`);
   242→    }
   243→
   244→    // 6. Apply sorting
   245→    const ascending = sort_order === 'asc';
   246→    query = query.order(sort_by as any, { ascending });
   247→
   248→    // 7. Apply pagination
   249→    query = query.range(offset, offset + limit - 1);
   250→
   251→    // 8. Execute query
   252→    console.log(`📋 Fetching users: limit=${limit}, offset=${offset}, role=${role}, is_active=${is_active}, search=${search}`);
   253→    const { data, error, count } = await query;
   254→
   255→    if (error) {
   256→      console.error('❌ Query failed:', error.message);
   257→      return NextResponse.json(
