   280→        limit,
   281→        offset,
   282→        has_more,
   283→      },
   284→    };
   285→
   286→    console.log(`✅ Fetched ${users.length} users (total: ${totalCount})`);
   287→    return NextResponse.json(response, { status: 200 });
   288→  } catch (error) {
   289→    console.error('❌ Unexpected error:', error);
   290→    return NextResponse.json(
   291→      { error: 'Internal server error' },
   292→      { status: 500 }
   293→    );
   294→  }
   295→}
   296→
   297→// Main POST handler
