    autoRefreshToken: false,
    persistSession: false,
  },
});

async function checkAllStudents() {
  try {
    // Get all users with student role
    const { data: users, error: usersError } = await supabase
      .from('users_profile')
      .select('id, email, full_name');

    if (usersError) throw usersError;

    // Get modules
    const { data: modules, error: modulesError } = await supabase
      .from('modules')
      .select('id, order, title')
      .order('order', { ascending: true });

    if (modulesError) throw modulesError;

    // Get lessons
    const { data: lessons, error: lessonsError } = await supabase
      .from('lessons')
      .select('id, module_id, order, title')
      .order('module_id, order', { ascending: true });

    if (lessonsError) throw lessonsError;

    const module2Id = modules.find((m) => m.order === 2)?.id;
    const module2Lessons = lessons.filter((l) => l.module_id === module2Id);

