const supabase = createClient(supabaseUrl, serviceRoleKey, {
  auth: {
    autoRefreshToken: false,
    persistSession: false,
  },
});

async function fixLessonOrders() {
  try {
    console.log('Finding Module 3...');
    const { data: modules } = await supabase
      .from('modules')
      .select('id, order, title')
      .eq('order', 3)
      .single();

    if (!modules) {
      console.error('Module 3 not found');
      return;
    }

    console.log(`Module 3 found: ${modules.id}`);

    // Get all lessons for Module 3
    const { data: lessons } = await supabase
      .from('lessons')
      .select('id, order, title')
      .eq('module_id', modules.id)
      .order('order', { ascending: true });

    if (!lessons) {
      console.error('No lessons found for Module 3');
      return;
    }

    console.log('\nCurrent Module 3 lessons:');
    lessons.forEach((lesson) => {
      console.log(`  - Order ${lesson.order}: ${lesson.title}`);
    });

    // Fix the orders - should start from 1, not 0
    console.log('\nFixing lesson orders...');
    for (let i = 0; i < lessons.length; i++) {
      const newOrder = i + 1;
      const { error } = await supabase
        .from('lessons')
        .update({ order: newOrder })
        .eq('id', lessons[i].id);

      if (error) {
        console.error(
          `Error updating lesson ${lessons[i].id}: ${error.message}`
        );
      } else {
        console.log(
          `✓ Updated "${lessons[i].title}" order from ${lessons[i].order} to ${newOrder}`
        );
      }
    }

    console.log('\n✅ Lesson orders fixed!');
  } catch (error) {
    console.error('Error:', error);
  }
}

fixLessonOrders();

