const { createClient } = require('@supabase/supabase-js');

const supabaseUrl = 'https://dfisdvkipzycpvesynkx.supabase.co';
const serviceRoleKey = 'sb_secret_sTR4-2H_5rKh2FXqoad63Q_qOacJuqq';

const supabase = createClient(supabaseUrl, serviceRoleKey, {
  auth: {
    autoRefreshToken: false,
    persistSession: false,
  },
});

async function fixAllLessonOrders() {
  try {
    // Get all modules
    const { data: modules } = await supabase
      .from('modules')
      .select('id, "order", title')
      .order('"order"', { ascending: true });

    if (!modules) {
      console.error('No modules found');
      return;
    }

    console.log('Checking and fixing lesson orders for all modules...\n');

    for (const module of modules) {
      // Get lessons for this module
      const { data: lessons } = await supabase
        .from('lessons')
        .select('id, "order", title')
        .eq('module_id', module.id)
        .order('"order"', { ascending: true });

      if (!lessons || lessons.length === 0) {
        console.log(`Module ${module.order}: No lessons`);
        continue;
      }

      // Check if lessons start with order 0 or have gaps
      const hasZeroOrder = lessons.some((l) => l['order'] === 0);
      const startingOrder = Math.min(...lessons.map((l) => l['order']));

      if (hasZeroOrder || startingOrder !== 1) {
        console.log(`Module ${module.order}: ${module.title}`);
        console.log(`  ⚠️  Found issue: lessons start with order ${startingOrder}`);

        // Fix the orders
        for (let i = 0; i < lessons.length; i++) {
          const newOrder = i + 1;
          const { error } = await supabase
            .from('lessons')
            .update({ "order": newOrder })
            .eq('id', lessons[i].id);

          if (error) {
            console.error(
              `  ❌ Error updating lesson ${lessons[i].id}: ${error.message}`
            );
          } else {
            console.log(
              `  ✓ Fixed "${lessons[i].title}": ${lessons[i]['order']} → ${newOrder}`
            );
          }
        }
      } else {
        console.log(`Module ${module.order}: ${module.title}`);
        console.log(`  ✓ OK - lessons properly ordered (1-${lessons.length})`);
      }
      console.log();
    }

    console.log('✅ All lesson orders checked and fixed!');
  } catch (error) {
    console.error('Error:', error);
  }
}

fixAllLessonOrders();

